import React, { useState } from 'react'; //Necessário sempre que for usar um html dentro do js (ainda que nn chame o react)
import api from './services/api';
import './App.css';

import logo from './assets/logo.svg';

function App() {
  const [email, setEmail] = useState(''); /*Conceito de Estado em React*/

  async function handleSubmit(event){
    event.preventDefault(); //Não redirecionará/atualizará a pagina

    const response = await api.post('/sessions', {
      email : email
    });

    const { _id } = response.data;
    console.log(_id);
    localStorage('user', _id);

    //console.log(response)

  }

  return (
    <div className="container">
      <img src={logo} alt="AirCnC"/>

      <div className="content">
        <p>
          Ofereça <strong>spots</strong> para programadores e encontre <strong>talentos</strong> para sua empresa.
        </p>

        {/* ----------FORMULARIO----------- */}
        {/* Sempre que for usar coisas do js no html devemos colocar chaves (Funções, variaveis, etc) */}
        <form onSubmit={handleSubmit}> 
          <label htmlFor="email">E-MAIL *</label>
          <input 
            type="email"
            id="email" 
            placeholder="Seu melhor e-mail"
            value={email}
            onChange={event => setEmail(event.target.value)}
          />
          <button className="btn" type="submit">Entrar</button>

        </form>
      </div>

    </div>

  );
}

export default App;
